var Vector = this.load('Vector');

var Rect = function (w, h, x, y) {
    this.x = x || 0; 
    this.y = y || 0;
    this.w = w;
    this.h = h;
};

//return a new rect of given width and height, 
//with it's top left corner at the given offset.
//relative to my top left corner.
Rect.prototype.rect = Vector.vectorizeArgs(function (d, o) {
    o = o || Vector.zero;
    o = o.add(this.x, this.y);
    return new Rect(d.x, d.y, o.x, o.y);
});

//return a new rect of given delta-width and offset
//relative current corner
Rect.prototype.delta  = Vector.vectorizeArgs(function (dd, o) {
    return this.rect(dd.add(this.w, this.h), o);    
});

Rect.prototype.offset = Vector.vectorizeArgs(function (o) {
    return this.rect(this.w, this.h, o.x, o.y);
});

//return a rect with the edges altered in their 
//respective directions
Rect.prototype.edges = function (l, t, r, b) {
    return this.delta(l + r, t + b, -l, -t);
};

//rect that is inset a certain number of pixels
//from each edge of the original.
Rect.prototype.inset = function (i) {
    return this.edges(-i, -i, -i, -i);
};
//rect that is outset a certain number of pixels
//from each edge of the original
Rect.prototype.outset = function (o) {
    return this.edges(o,o,o,o);
};

Rect.prototype._part = Vector.vectorizeArgs(function (part, offset) {
    var dims = new Vector(this.w, this.h);
    part = part.pairwise(function (a, b) {
        if (a < -b) return 0;
        if (-b <= a && a <= 0) return b + a;
        if (0 < a && a <= b) return a;
        if (b < a) return b;
    }, dims);
    offset = offset.pairwise(function (a, b) {
        if (a < -b) return 0;
        if (-b <= a && a < 0 ) return b + a;
        if (0 <= a && a <= b) return a;
        if (b < a ) return b;
    }, dims);
    offset = offset.pairwise(function (a, b) {
        if (b < a ) return b;
        return a;
    }, dims.sub(part));
    return this.rect(part, offset);
});
Rect.prototype.left    = function (z) { return this._part(z, 0, 0, 0); }; 
Rect.prototype.top     = function (z) { return this._part(0, z, 0, 0); }; 
Rect.prototype.right   = function (z) { return this._part(z, 0, -z, 0); };
Rect.prototype.bottom  = function (z) { return this._part(0, z, 0, -z); }; 
Rect.prototype.rleft   = function (z) { return this.right(-z); }; 
Rect.prototype.rtop    = function (z) { return this.bottom(-z); }; 
Rect.prototype.rright  = function (z) { return this.left(-z); }; 
Rect.prototype.rbottom = function (z) { return this.top(-z); }; 


Rect.prototype._slices = function (edge, dim, width, first) {
    width = width || this.w;
    first = first || width;
    var remfn = 'r' + edge,
        slices = [ this[edge](first) ],
        failsafe = 0;
        remainder = this[remfn](first);
    while (remainder[dim] > 0) {
        if (++failsafe > 100) break;
        slices.push(remainder[edge](width));
        remainder = remainder[remfn](width);
    }
    return slices;
};
Rect.prototype.slicesFromLeft = function (width, first) {
    return this._slices('left', 'w', width, first);
};
Rect.prototype.slicesFromRight = function (width, first) {
    return this._slices('right', 'w', width, first);
};
Rect.prototype.slicesFromTop = function (width, first) {
    return this._slices('top', 'h', width, first);    
};
Rect.prototype.slicesFromBottom = function (width, first) {
    return this._slices('bottom', 'h', width, first);
};


return Rect;

